import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Observable } from 'rxjs';
import { ProductosService } from '../productos.service';

@Component({
  selector: 'app-productos-list',
  templateUrl: './productos-list.component.html',
  styleUrls: ['./productos-list.component.sass']
})
export class ProductosListComponent {
  productosList$!:Observable<any[]>;
  _formGroup :FormGroup;
  data: any[] = [];

  constructor(private _formBuilder:FormBuilder, private _service:ProductosService){
    this._formGroup = this._formBuilder.group({
      codigoProducto:[' ', [Validators.required]],
      descripcion:[' ', [Validators.required]],
      precio:[' ', [Validators.required]],
      existecia:[' ', [Validators.required]]
    })
  }

  ngOnInit(): void{
    this.productosList$ = this._service.listProducts();
  }
}
<div class="d-flex justify-content-between py-3">                    
    <div class="h4">Administración de Productos                       
    </div>            
    <div>
        <button type="button" class="btn btn-outline-primary"
          role="button" data-bs-toggle="modal" data-bs-target="#exampleModal">
          Crear Nuevo
        </button>
    </div>                    
</div>            
<div class="row">
    <div class="card border-0 shadow-lg">
        <div class="card-body">
            <table class="table table-striped table-hover">
                <thead>
                    <tr>
                        <th>CODIGO</th>
                        <th>DESCRIPCION</th>
                        <th>PRECIO</th>
                        <th>STOCK</th>
                        <th> </th>
                    </tr>
                </thead>

                <tbody *ngFor="let item of this.productosList$|async">
                    <tr>
                        <td>{{ item.codigoProducto }}</td>
                        <td>{{ item.descripcion }}</td>
                        <td>{{ item.precio }}</td>
                        <td>{{ item.existencia }}</td>
                        <td>

                        </td>
                    </tr>
                </tbody>
            </table>
        </div>
    </div>
</div>
