import { Component } from '@angular/core';
import { Observable } from 'rxjs';
import { UsuariosService } from '../usuarios.service';

@Component({
  selector: 'app-usuarios-list',
  templateUrl: './usuarios-list.component.html',
  styleUrls: ['./usuarios-list.component.sass']
})
export class UsuariosListComponent {
  usuariosList$!:Observable<any[]>;

  constructor(private service:UsuariosService){}

  ngOnInit(): void{
    this.usuariosList$ = this.service.viewUsuarios();
  }

  deleteUsuario(id:number){
    if(confirm("Seguro desea eliminar este registro?")){
      this.service.destroyUsuario(id)
      .subscribe(book=>{
        this.usuariosList$ = this.service.viewUsuarios();
      })
    }  
  }
}
<div class="container-fluid mt-3">
    <p>Modulo de Administración</p>
    <div class="d-flex justify-content-between py-3">

        <div class="h4">Administración de Usuarios</div>

        <div><a href="usuarios/usuarios-add" class="btn btn-primary">Crear Nuevo Usuario</a></div>
    </div>

    <div class="row">
        <div class="card border-0 shadow-lg">
            <div class="card-body">
                <table class="table table-striped table-hover">
                    <thead>
                        <tr>
                            <th>USUARIO</th>
                            <th>PRIMER NOMBRE</th>
                            <th>SEGUNDO NOMBRE</th>
                            <th>PRIMER APELLIDO</th>
                            <th>SEGUNDO APELLIDO</th>
                            <th></th>
                        </tr>
                    </thead>

                    <tbody *ngFor="let item of this.usuariosList$|async">
                        <tr>
                            <td>{{ item.usuario }}</td>
                            <td>{{ item.primerNombre }}</td>
                            <td>{{ item.segundoNombre }}</td>
                            <td>{{ item.primerApellido }}</td>
                            <td>{{ item.segundoApellido }}</td>
                            <td>
                                <a routerLink="/usuarios/edit/{{ item.id }}" class="btn btn-success">Edit</a>
                                <button type="button" (click)='deleteUsuario(item.id)' class="btn btn-danger">Delete</button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
</div>
