import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ServiceService } from '../service.service';
import { ActivatedRoute } from '@angular/router';
import { Cargo } from '../cargo';

@Component({
  selector: 'app-cargos-edit',
  templateUrl: './cargos-edit.component.html',
  styleUrls: ['./cargos-edit.component.sass']
})
export class CargosEditComponent implements OnInit {
  formGroup !:FormGroup;

  constructor(private _route: ActivatedRoute, private formbuilder:FormBuilder, private service:ServiceService) { }

  id:any;
  cargo:Cargo;
  loscargo : any;

  UsuariosList: any;
  SelectedValue:any;
  changeUsuarios(e){
    console.log(e.target.value);
    this.SelectedValue=e.target.value;
  }

  dataIn: any[] = [];
  data: any;

  getCargosdata(){
    this.service.viewCargos().subscribe(response => {
      this.loscargo = response;
    })
  }

ngOnInit(): void{
  //this.loadData();
  this.getCargosdata();
  this.formGroup = this.formbuilder.group({
    id:[' '],
    codigo:[' ',[Validators.required]],
    nombre:[' ',[Validators.required]],
    activo:[' ',[Validators.required]],
    idUsuario:[' ',[Validators.required]]
  })
  
  this.id = this._route.snapshot.params['id'];
  //this.getDataId();
  this.service.getUsuarios().subscribe((data: any) => {
    this.UsuariosList = data;    
  })
}

editCargo(car : Cargo) {
  this.formGroup.controls['id'].setValue(car.id);
  this.formGroup.controls['name'].setValue(car.nombre);
  this.formGroup.controls['activo'].setValue(car.activo);
  this.formGroup.controls['idUsuario'].setValue(car.idUsuario);

}
