  selector: 'app-cargos-add',
  templateUrl: './cargos-add.component.html',
  styleUrls: ['./cargos-add.component.sass']
})
export class CargosAddComponent implements OnInit {

  title='chaman gautam';
datasaved=false;
bookForm:FormGroup;
allbooks:Observable<Cargo[]>;
