import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ServiceService } from '../service.service';
import { ActivatedRoute } from '@angular/router';
import { Cargo } from '../cargo';

@Component({
  selector: 'app-cargos-edit',
  templateUrl: './cargos-edit.component.html',
  styleUrls: ['./cargos-edit.component.sass']
})
export class CargosEditComponent implements OnInit {
  formGroup :FormGroup;

  constructor(private _route: ActivatedRoute, private formbuilder:FormBuilder, private service:ServiceService) { 
    this.formGroup = this.formbuilder.group({
      codigo:[' ',[Validators.required]],
      nombre:[' ',[Validators.required]],
      activo:[' ',[Validators.required]],
      idUsuario:[' ',[Validators.required]]
    })
  }

  id:any;
  cargo:Cargo;

  UsuariosList: any;
  SelectedValue:any;
  changeUsuarios(e){
    console.log(e.target.value);
    this.SelectedValue=e.target.value;
  }

  dataIn: any[] = [];
  data: any;

ngOnInit(): void{
  this.loadData();

  
  this.id = this._route.snapshot.params['id'];
  this.getDataId();
  this.service.getData().subscribe((data: any) => {
    this.UsuariosList = data;
  })

}


getDataId(){
  this.service.getCargosId(this.id).subscribe(response => {{
    //console.log(response);
    this.data = response;
    this.cargo = this.data;
  }});
}

  onSubmit(){
    console.log(this.formGroup.value);
    
  }

  loadData() {
    this.service.getData().subscribe((result) => {
      this.dataIn = result;
    });
  }
}
