import { Component, OnInit } from '@angular/core';
import { ServiceService } from './service.service';
import { Cargo } from './cargo';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-cargos',
  templateUrl: './cargos.component.html',
  styleUrls: ['./cargos.component.sass']
})
export class CargosComponent implements OnInit {
  cargosList$!:Observable<any[]>;

  constructor(private service:ServiceService){}

  ngOnInit(): void{
      this.cargosList$ = this.service.viewCargos();
  }

  deleteCargo(event:any, cargoId:Number){
    if(confirm('Seguro desea eliminar este dato?')){
      event.target.innerText = "Eliminando";

      this.service.destroyCargo(cargoId).subscribe((res:any) => {
        this.cargosList$ = this.service.viewCargos();
      });
    }
  }
}
