import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, FormControl, Validators } from '@angular/forms';
import { AnonymousSubject } from 'rxjs/internal/Subject';
import { FormsModule } from '@angular/forms';
import { Observable } from 'rxjs';
import { Cargo } from '../cargo';
import { ServiceService } from '../service.service';

@Component({
  selector: 'app-cargos-add',
  templateUrl: './cargos-add.component.html',
  styleUrls: ['./cargos-add.component.sass']
})
export class CargosAddComponent implements OnInit {
  cargosList$!:Observable<any[]>;

  title='chaman gautam';
  datasaved=false;
  formGroup :FormGroup;
  allbooks:Observable<Cargo[]>;
  BookitoUpdate=null;

constructor(private formbuilder:FormBuilder, private service:ServiceService) { }
ngOnInit(){
  this.formGroup=this.formbuilder.group({
    codigo:[' ',[Validators.required]],
    nombre:[' ',[Validators.required]],
    activo:[' ',[Validators.required]],
    idUsuario:[' ',[Validators.required]]
  });

  this.cargosList$ = this.service.viewCargos();
}

onFormSubmit(){
  this.datasaved=false;
  let book=this.formGroup.value;
  this.createbooks(book);
  this.formGroup.reset();
}

createbooks(cargo:Cargo){
  if(this.BookitoUpdate==null){

    this.service.saveCargo(cargo).subscribe(cargo=>{
      this.datasaved=true;
      this.cargosList$ = this.service.viewCargos();

    });
}

}
}
