import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Cargo } from './cargo';

@Injectable({
  providedIn: 'root'
})

export class ServiceService {

  readonly apiURL = 'http://127.0.0.1:8000/api/cargos/';

  constructor(private httpClient:HttpClient) { }

  viewCargos():Observable<any[]> {
    return this.httpClient.get<any>(`http://127.0.0.1:8000/api/cargos`);
  }

  saveCargo(inputData: object){
    return this.httpClient.post(this.apiURL + '/cargos-add', inputData);
  }

  destroyCargos(id: Number){
    return this.httpClient.delete(`http://127.0.0.1:8000/api/cargos/${id}/delete`);
  }

  destroyCargo(itemId: number): Observable<any> {
    const url = `${this.apiUrl}/${itemId}/delete`;
    return this.httpClient.delete(url);
  }
}
