import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Cargo } from './cargo';

@Injectable({
  providedIn: 'root'
})
export class ServiceService {

  readonly apiURL = 'http://127.0.0.1:8000/api';

  constructor(private httpClient:HttpClient) { }

  viewCargos() {
    return this.httpClient.get(`http://127.0.0.1:8000/api/cargos`);
  }

  saveCargo(inputData: object){
    return this.httpClient.post(this.apiURL + '/cargos-add', inputData);
  }

  destroyCargo(cargoId: Number){
    return this.httpClient.delete(this.apiURL + '/delete/${cargoId}');
  }
}
